import styles from "./Card.module.scss";

interface CardProps {
   pokemonId?: number;
   pokemonName?: string;
   pokemonImage?: string;
   pokemonTypes: [];
   pokemonValue?: string;
}

function Card({
   pokemonId,
   pokemonName,
   pokemonImage,
   pokemonValue,
   pokemonTypes,
}: CardProps) {
   async function handleSellTransaction() {
      const payload = {
         pokemonId: pokemonId,
         pokemonName: pokemonName,
         pokemonValue: pokemonValue,
         // axios.post('/transaction', )
      };
   }

   return (
      <div className={styles.card}>
         <span className={styles.imageContainer}>
            <img src={pokemonImage}></img>
         </span>
         <span className={styles.infoContainer}>
            <span className={styles.petInformation}>
               <h3>{pokemonName?.toUpperCase()}</h3>
               {/* {pokemonTypes.map((type) => {
                  console.log(type, "TYPE");
                  return <p>{type as string}</p>;
               })} */}
               <p>Valor: {pokemonValue}</p>
            </span>

            <span className={styles.actionButtonsContainer}>
               {/* //PASSAR PROPS DO USER QUE RECEBERA A FUNCAO PELA FUNCAO DO CONTEXT */}

               <button
                  onClick={handleSellTransaction}
                  className={`${styles.actionButton} ${styles.buy}`}
               >
                  Vender
               </button>
            </span>
         </span>
      </div>
   );
}

export default Card;
