import axios from "axios";
import { useEffect, useState } from "react";
import styles from "../card/Card.module.scss";

interface CardProps {
   pokemonId?: number;
   pokemonName?: string;
   pokemonImage?: string;
   baseExperience?: number;
   BTCCurrencyAtTrade: number;
   tradeValueAtOperationDate: number;
   transactionDate: Date;
}

function WalletCard({
   pokemonId,
   BTCCurrencyAtTrade,
   tradeValueAtOperationDate,
   transactionDate,
   baseExperience,
}: CardProps) {
   const [pokemonInfo, setPokemonInfo] = useState<{
      pokemonId?: string;
      pokemonImage?: string;
      pokemonName?: string;
   }>();

   async function handleSellTransaction() {
      const payload = {};
   }

   (async function getPokemonInfo() {
      try {
         await axios
            .post(`http://localhost:3000/pokemons/`, { id: pokemonId })
            .then((res) => {
               setPokemonInfo(res.data);
            });
      } catch (err) {
         console.log(err, "ERR");
      }
   })();
   console.log(BTCCurrencyAtTrade, "BTC");
   console.log(baseExperience, "pokemonValue");
   return (
      <div className={styles.card}>
         <span className={styles.imageContainer}>
            <img src={pokemonInfo?.pokemonImage} alt="pokemon"></img>
         </span>
         <span className={styles.infoContainer}>
            <span className={styles.pokemonInformation}>
               <h3>{pokemonInfo?.pokemonName?.toUpperCase()}</h3>

               <p>Valor pago: {tradeValueAtOperationDate}</p>
               <p>Valor atual: {BTCCurrencyAtTrade * baseExperience} </p>
            </span>

            <span className={styles.actionButtonsContainer}>
               <button
                  onClick={handleSellTransaction}
                  className={`${styles.actionButton} ${styles.buy}`}
               >
                  $ Vender
               </button>
            </span>
         </span>
      </div>
   );
}

export default WalletCard;
