import axios from "axios";
import React from "react";
import Button from "../button/singleButton";
import styles from "./Card.module.scss";

interface CardProps {
   handleBuySubmit: (payload: any) => void;
   pokemonId?: number;
   pokemonName?: string;
   pokemonImage?: string;
   pokemonValue?: string;
   pokemonCurrentValue?: number;
}

function Card({
   pokemonId,
   pokemonName,
   pokemonImage,
   pokemonValue,
   pokemonCurrentValue,
   handleBuySubmit,
}: CardProps) {
   function handleBuyTransaction() {
      const payload = {
         baseExperience: pokemonValue,
         pokemonId,
         pokemonName: pokemonName,
      };
      handleBuySubmit(payload);
   }

   return (
      <div className={styles.card}>
         <span className={styles.imageContainer}>
            <img src={pokemonImage}></img>
         </span>
         <span className={styles.infoContainer}>
            <span className={styles.pokemonInformation}>
               <h3>{pokemonName?.toUpperCase()}</h3>

               <p>Valor do pokemon: {pokemonCurrentValue?.toFixed(4)}</p>
            </span>

            <span className={styles.actionButtonsContainer}>
               <button
                  onClick={handleBuyTransaction}
                  className={`${styles.actionButton} ${styles.buy}`}
               >
                  Comprar
               </button>
            </span>
         </span>
      </div>
   );
}

export default Card;
