import axios from "axios";
import React, { Dispatch, SetStateAction, useState } from "react";

import Button from "../button/singleButton";
import Card from "./Card";
import { storageToken } from "../../contexts/authContext";
import styles from "./CardList.module.scss";

interface Props {
   money: number;
   setMoney: Dispatch<SetStateAction<number>>;
}
function CardList({ money, setMoney }: Props) {
   const [pokemons, setPokemons] = useState<[]>();
   let valueMap: Array<number> = [];
   const [pokemonInfo, setPokemonInfo] = useState<{
      pokemonImage: string;
      baseExperience: number;
      pokemonCurrentValue: number;
      pokemonId: number;
   }>();

   async function getPokemons() {
      const res = await axios.get("http://localhost:3000/pokemons");
      setPokemons(res.data.pokemonList);
      pokemons?.map((pokemon) => {
         getPokemonInfo(pokemon);
      });
   }
   getPokemons();

   async function handleBuySubmit(payload: any) {
      const token = storageToken();

      const transactionResponse = await axios.post(
         "http://localhost:3000/transactions/pokemon/buy",
         payload,
         {
            headers: {
               ["x-access-token"]: token,
            },
         }
      );

      const diff = money - transactionResponse.data.acquisitionUSDValue;
      setMoney(money - diff);
   }

   async function getPokemonInfo(pokemon: {
      id: number;
      name: string;
      image: string;
      baseExperience: string;
      pokemonCurrentValue: string;
   }) {
      const res = await axios.post(`http://localhost:3000/pokemons/`, {
         id: pokemon.id,
         baseExperience: pokemon?.baseExperience,
      });
      const pokemonId = pokemon.id;

      valueMap[pokemonId] = res.data.pokemonCurrentValue;
      console.log(valueMap[pokemonId], "Valuemap");
      setPokemonInfo(res.data);
   }
   return (
      <div className={styles.container}>
         <ul className={styles.cardList}>
            {pokemons?.map(
               (pokemon: {
                  id: number;
                  name: string;
                  image: string;
                  baseExperience: string;
                  pokemonCurrentValue: string;
               }) => {
                  return (
                     <Card
                        key={pokemon.id}
                        handleBuySubmit={handleBuySubmit}
                        pokemonId={pokemon?.id}
                        pokemonName={pokemon?.name}
                        pokemonImage={pokemon?.image}
                        pokemonValue={pokemon?.baseExperience}
                        pokemonCurrentValue={valueMap[pokemon.id]}
                     />
                  );
               }
            )}
         </ul>
         <div className={styles.navigationPagesContainer}>
            <Button type="button">Página Anterior</Button>
            <Button type="button">Próxima Página</Button>
         </div>
      </div>
   );
}

export default CardList;
