import React from "react";
import styles from "./Card.module.scss";

interface CardProps {
   pokemonName?: string;
   pokemonImage?: string;
   // pokemonTypes: [];
   pokemonValue?: string;
}

function Card({ pokemonName, pokemonImage, pokemonValue }: CardProps) {
   return (
      <div className={styles.card}>
         <span className={styles.imageContainer}>
            <img src={pokemonImage}></img>
         </span>
         <span className={styles.infoContainer}>
            <span className={styles.petInformation}>
               <h3>{pokemonName?.toUpperCase()}</h3>
               {/* {pokemonTypes.map((type) => {
                  return <p>type.</p>;
               })} */}
               <p>Valor: {pokemonValue}</p>
            </span>

            <span className={styles.actionButtonsContainer}>
               {/* //PASSAR PROPS DO USER QUE RECEBERA A FUNCAO PELA FUNCAO DO CONTEXT */}

               <a className={`${styles.actionButton} ${styles.buy} `}>
                  Comprar{" "}
               </a>

               <a className={`${styles.actionButton} ${styles.sell}`}>
                  Vender{" "}
               </a>
            </span>
         </span>
      </div>
   );
}

export default Card;
