import axios from "axios";
import { useEffect, useState } from "react";
import styles from "./Card.module.scss";

interface CardProps {
   pokemonId?: number;
   pokemonName?: string;
   pokemonImage?: string;
   pokemonValue?: string;
   BTCCurrencyAtTrade: string;
   monetaryValue: number;
   transactionDate: Date;
}

function Card({
   pokemonId,
   BTCCurrencyAtTrade,
   monetaryValue,
   transactionDate,
}: CardProps) {
   const [pokemonInfo, setPokemonInfo] = useState<{
      pokemonId?: string;
      pokemonImage?: string;
      pokemonName?: string;
   }>();

   async function handleSellTransaction() {
      const payload = {};
   }

   useEffect(() => {
      (async function getPokemonInfo() {
         try {
            await axios
               .post(`http://localhost:3000/pokemons/`, { id: pokemonId })
               .then((res) => {
                  setPokemonInfo(res.data);
               });
         } catch (err) {
            console.log(err, "ERR");
         }
      })();
   }, [monetaryValue]);

   return (
      <div className={styles.card}>
         <span className={styles.imageContainer}>
            <img src={pokemonInfo?.pokemonImage} alt="pokemon"></img>
         </span>
         <span className={styles.infoContainer}>
            <span className={styles.pokemonInformation}>
               <h3>{pokemonInfo?.pokemonName?.toUpperCase()}</h3>

               <p>Valor: {monetaryValue?.toFixed(2)}</p>
            </span>

            <span className={styles.actionButtonsContainer}>
               {/* //PASSAR PROPS DO USER QUE RECEBERA A FUNCAO PELA FUNCAO DO CONTEXT */}

               <button
                  onClick={handleSellTransaction}
                  className={`${styles.actionButton} ${styles.buy}`}
               >
                  $ Vender
               </button>
            </span>
         </span>
      </div>
   );
}

export default Card;
