import axios from "axios";
import { useEffect, useState } from "react";
import { storageToken } from "src/contexts/authContext";
import styles from "../card/Card.module.scss";

interface CardProps {
   pokemonId?: number;
   pokemonName?: string;
   pokemonImage?: string;
   baseExperience?: number;
   btcCurrencyAtAcquisition?: number;
   tradeValueAtOperationDate?: number;
   transactionId?: string;
   acquisitionUSDValue?: number;
   acquisitionDate?: Date;
}

function WalletCard({
   pokemonId,
   pokemonName,
   tradeValueAtOperationDate,
   transactionId,
   acquisitionUSDValue,
   acquisitionDate,
   baseExperience,
}: CardProps) {
   const [pokemonInfo, setPokemonInfo] = useState<{
      pokemonImage: string;
      pokemonCurrentValue: number;
   }>();

   async function handleSellTransaction() {
      const token = storageToken();
      const payload = {
         transactionId,
      };

      await axios
         .post("http://localhost:3000/transactions/pokemon/sell", payload, {
            headers: {
               ["x-access-token"]: token,
            },
         })
         .then((res) => {
            console.log(res, "RES");
         });
   }

   useEffect(() => {
      if (!baseExperience) {
         (async function getPokemonInfo() {
            try {
               await axios
                  .post(`http://localhost:3000/pokemons/`, {
                     id: pokemonId,
                     transaction: baseExperience,
                  })
                  .then((res) => {
                     setPokemonInfo(res?.data);
                  });
            } catch (err) {
               console.log(err, "ERR");
            }
         })();
      }
   }, [baseExperience]);

   console.log(pokemonInfo, "POKEINFO");
   const acquisitionDateString = new Date(
      acquisitionDate || ""
   ).toLocaleDateString();
   return (
      <div className={styles.card}>
         <span className={styles.imageContainer}>
            <img src={pokemonInfo?.pokemonImage} alt="pokemon"></img>
         </span>
         <span className={styles.infoContainer}>
            <span className={styles.pokemonInformation}>
               <h3>{pokemonName?.toUpperCase()}</h3>

               <p>Valor atual: {pokemonInfo?.pokemonCurrentValue}</p>
            </span>
            <span className={styles.moreInfo}>
               <p>Adquirido em: {acquisitionDateString}</p>
               <p>Adquirido por: {acquisitionUSDValue?.toFixed(2)} d√≥lares</p>
            </span>
            <span className={styles.actionButtonsContainer}>
               <button
                  onClick={handleSellTransaction}
                  className={`${styles.actionButton} ${styles.buy}`}
               >
                  $ Vender
               </button>
            </span>
         </span>
      </div>
   );
}

export default WalletCard;
