import React from "react";
import styles from "./Card.module.scss";
import DungaImage from "../../assets/images/Imagem Dunga.svg";
import MessageIcon from "../../assets/images/MessageUserIcon.svg";
import { Link } from "react-router-dom";

interface CardProps {
   pokemonName?: string;
   pokemonImage?: string;
   // pokemonTypes: [];
   pokemonValue?: string;
}

function Card({
   pokemonName,
   pokemonImage,

   pokemonValue,
}: CardProps) {
   return (
      <div className={styles.card}>
         <span className={styles.imageContainer}>
            <img src={pokemonImage}></img>
         </span>
         <span className={styles.infoContainer}>
            <span className={styles.petInformation}>
               <h3>{pokemonName}</h3>
               {/* {pokemonTypes.map((type) => {
                  return <p>type.</p>;
               })} */}
               <p>{pokemonValue} </p>
            </span>

            <span className={styles.locationContainer}>
               <p className={styles.cityLocation}>São Gonzalo (RJ)</p>
               <Link to={"/message"}>
                  <span className={styles.message}>
                     {/* //PASSAR PROPS DO USER QUE RECEBERA A FUNCAO PELA FUNCAO DO CONTEXT */}
                     <img src={MessageIcon}></img>
                     <p>Falar com responsável</p>
                  </span>
               </Link>
            </span>
         </span>
      </div>
   );
}

export default Card;
