import Header from "../../components/header";
import Footer from "../../components/footer";
import TextSection from "../../components/textSection";
import MainLogo from "../../components/mainLogo";
import styles from "./HomePage.module.scss";
import CardList from "../../components/card/CardList";
import { useEffect, useState } from "react";
import axios from "axios";

function HomePage() {
   const storageToken = localStorage.getItem("tokenObj") || "";

   const userToken = JSON.parse(storageToken).token;
   const [userWallet, setUserWallet] = useState<{ money: number }>();
   const [userMoney, setUserMoney] = useState<number>();
   useEffect(() => {
      (async function () {
         await axios
            .get("http://localhost:3000/users", {
               headers: {
                  ["x-access-token"]: userToken,
               },
            })
            .then((res) => {
               setUserWallet(res.data.wallet);
               setUserMoney(res.data.wallet.money.toFixed(2));
            });
      })();
   }, []);

   return (
      <div className={styles.HomePageIndex}>
         <Header />
         <MainLogo />
         <TextSection
            blueText={true}
            text="Bem vindo ao BxMon - PokeCarteira! Veja aqui os pokemons disponíveis para transacionar!"
         />
         <div className={styles.userInfo}>
            <h1>Você tem R$ {userMoney}</h1>
            <span></span>
         </div>
         <CardList userMoney={userMoney || 0} />

         <Footer />
      </div>
   );
}

export default HomePage;
