import Header from "../../components/header";
import Footer from "../../components/footer";
import TextSection from "../../components/textSection";
import MainLogo from "../../components/mainLogo";
import styles from "./SummaryPage.module.scss";
import CardList from "../../components/card/CardList";
import { useEffect, useState } from "react";
import axios from "axios";
import { storageToken } from "../../contexts/authContext";
import MainLogoImage from "../../assets/images/BxMon.png";
function SummaryPage() {
   const [userWallet, setUserWallet] = useState<{ money: number }>();
   const token = storageToken();

   useEffect(() => {
      (async function () {
         await axios
            .get("http://localhost:3000/users/wallet", {
               headers: {
                  ["x-access-token"]: token,
               },
            })
            .then((res) => {
               setUserWallet(res.data);
            });
      })();
   }, [userWallet]);

   return (
      <div className={styles.HomePageIndex}>
         <Header />
         <MainLogo src={MainLogoImage} />

         <TextSection
            blueText={true}
            text="Bem vindo ao BxMon - PokeCarteira! Veja aqui os pokemons disponíveis para compra!"
         />
         <div className={styles.userInfo}>
            <h1>Você tem R$ {userWallet?.money}</h1>
            <span></span>
         </div>
         <CardList userMoney={userWallet?.money || 0} />

         <Footer />
      </div>
   );
}

export default SummaryPage;
