import axios from "axios";
import { useEffect, useState } from "react";
import { storageToken } from "src/contexts/authContext";
import styles from "./Summary.module.scss";

interface CardProps {
   pokemonId?: number;
   pokemonName?: string;
   pokemonImage?: string;
   baseExperience?: number;
   btcCurrencyAtAcquisition?: number;
   tradeValueAtOperationDate?: number;
   transactionId?: string;
   acquisitionUSDValue?: number;
   acquisitionDate?: Date;

   sellDate?: Date;
   sellUSDValue?: number;
}

function SummaryItem({
   pokemonId,
   pokemonName,
   tradeValueAtOperationDate,
   transactionId,
   acquisitionUSDValue,
   acquisitionDate,
   baseExperience,
   btcCurrencyAtAcquisition,
   sellUSDValue,
   sellDate,
}: CardProps) {
   const acquisitionDateString = new Date(
      acquisitionDate || ""
   ).toLocaleDateString();
   return (
      <div className={styles.summaryContainer}>
         <span className={styles.summaryTitle}>
            <ul></ul>

            <li>
               <span>
                  <>
                     <p>Pokemon: </p>
                     {pokemonName}
                  </>
               </span>
            </li>
            <li>
               <span>
                  <>
                     <p>Valor da compra em dólares: : </p>
                     {acquisitionUSDValue}
                  </>
               </span>
            </li>

            <li>
               <span>
                  <>
                     <p>Data da aquisição: </p>
                     {acquisitionDate}{" "}
                  </>
               </span>
            </li>
            <li>
               <span>
                  <>
                     <p>Tipo da transação:</p>
                     {sellDate ? "Venda" : "Compra"}
                  </>
               </span>
            </li>
            <li>
               <span>
                  <>
                     <p>Data da venda:</p>
                     {sellDate}
                  </>
               </span>
            </li>
         </span>
      </div>
   );
}

export default SummaryItem;
