import axios from "axios";
import { useEffect, useState } from "react";
import { storageToken } from "src/contexts/authContext";
import styles from "../card/Card.module.scss";

interface CardProps {
   pokemonId?: number;
   pokemonName?: string;
   pokemonImage?: string;
   baseExperience?: number;
   BTCCurrencyAtTrade?: number;
   tradeValueAtOperationDate?: number;
   transactionDate?: Date;
   transactionId?: string;
}

function WalletCard({
   pokemonId,
   BTCCurrencyAtTrade,
   tradeValueAtOperationDate,
   transactionId,
   baseExperience,
}: CardProps) {
   const [pokemonInfo, setPokemonInfo] = useState<{
      pokemonId?: string;
      pokemonImage?: string;
      pokemonName?: string;
   }>();

   async function handleSellTransaction() {
      const token = storageToken();
      const payload = {
         transactionId,
      };
      await axios.post(
         "http://localhost:3000/transactions/pokemon/buy",
         payload,
         {
            headers: {
               ["x-access-token"]: token,
            },
         }
      );
   }
   let boughtAtPrice = 0;
   if (BTCCurrencyAtTrade && baseExperience) {
      boughtAtPrice = BTCCurrencyAtTrade * baseExperience;
   }

   (async function getPokemonInfo() {
      try {
         await axios
            .post(`http://localhost:3000/pokemons/`, { id: pokemonId })
            .then((res) => {
               setPokemonInfo(res.data);
            });
      } catch (err) {
         console.log(err, "ERR");
      }
   })();

   return (
      <div className={styles.card}>
         <span className={styles.imageContainer}>
            <img src={pokemonInfo?.pokemonImage} alt="pokemon"></img>
         </span>
         <span className={styles.infoContainer}>
            <span className={styles.pokemonInformation}>
               <h3>{pokemonInfo?.pokemonName?.toUpperCase()}</h3>

               <p>Valor pago: {boughtAtPrice} </p>
               <p>Valor atual: {tradeValueAtOperationDate}</p>
            </span>

            <span className={styles.actionButtonsContainer}>
               <button
                  onClick={handleSellTransaction}
                  className={`${styles.actionButton} ${styles.buy}`}
               >
                  $ Vender
               </button>
            </span>
         </span>
      </div>
   );
}

export default WalletCard;
