import axios from "axios";
import React, { useEffect, useState } from "react";

import Button from "../button/singleButton";
import Card from "./Card";
import { storageToken } from "../../contexts/authContext";
import styles from "./CardList.module.scss";

interface Props {
   userMoney: number;
}
function CardList({ userMoney }: Props) {
   const [pokemons, setPokemons] = useState<[]>([]);
   const [nextPage, setNextPage] = useState<String>("");
   const [previousPage, setPreviousPage] = useState<String>("");

   useEffect(() => {
      (async function () {
         await axios.get("http://localhost:3000/pokemons").then((res) => {
            setPokemons(res.data.pokemonList);
            // setNextPage(res.data.pagination.next);
            // setPreviousPage(res.data.pagination.previous);
         });
      })();
   }, []);

   async function handleBuySubmit(payload: any) {
      const token = storageToken();

      await axios.post(
         "http://localhost:3000/transactions/pokemon/buy",
         payload,
         {
            headers: {
               ["x-access-token"]: token,
            },
         }
      );
   }

   return (
      <div className={styles.container}>
         <ul className={styles.cardList}>
            {pokemons?.map(
               (pokemon: {
                  id: number;
                  name: string;
                  image: string;
                  baseExperience: string;
               }) => {
                  return (
                     <Card
                        key={pokemon.id}
                        handleBuySubmit={handleBuySubmit}
                        pokemonId={pokemon?.id}
                        pokemonName={pokemon?.name}
                        pokemonImage={pokemon?.image}
                        pokemonValue={pokemon?.baseExperience}
                     />
                  );
               }
            )}
         </ul>
         <div className={styles.navigationPagesContainer}>
            <Button type="button">Página Anterior</Button>
            <Button type="button">Próxima Página</Button>
         </div>
      </div>
   );
}

export default CardList;
