import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import Button from "../button/singleButton";
import Card from "./WalletCard";
import styles from "./CardList.module.scss";

function WalletCardList() {
   // const [pokemons, setPokemons] = useState<[]>([]);
   // const [nextPage, setNextPage] = useState<String>("");
   // const [previousPage, setPreviousPage] = useState<String>("");
   const [transactions, setTransactions] = useState<
      [
         {
            _id: number;
            type: string;
            pokemonName: string;
            pokemonId: number;
            transactionDate: Date;
            monetaryValue: string;
            BTCCurrencyAtTrade: string;
         }
      ]
   >();

   const tokenInfo = localStorage.getItem("tokenObj") || "";

   const userToken = JSON.parse(tokenInfo).token;

   useEffect(() => {
      (async function () {
         await axios
            .get("http://localhost:3000/transactions", {
               headers: {
                  ["x-access-token"]: userToken,
               },
            })
            .then((res) => {
               // setPokemons(res.data.pokemonList);
               // setNextPage(res.data.pagination.next);
               // setPreviousPage(res.data.pagination.previous);
               setTransactions(res.data);
               console.log(transactions, "Transactions");
            });
      })();
   }, []);
   // console.log(transactions, "Transactions");
   // console.log(
   //    transactions?.filter((transaction) => transaction?.type === "buy")
   // );
   return (
      <div className={styles.container}>
         <div className={styles.userInfo}>
            <h1>Você tem P$ 1000,00</h1>
            <span></span>
         </div>
         <ul className={styles.cardList}>
            {/* {transactions
               ?.filter((transaction) => transaction?.type === "buy")
               ?.map((buyTransaction) => {
                  return (
                     <Card
                        pokemonId={buyTransaction?.pokemonId}
                        pokemonName={buyTransaction?.pokemonName}
                        BTCCurrencyAtTrade={buyTransaction?.BTCCurrencyAtTrade}
                        // pokemonImage={buyTransaction?.image}
                        monetaryValue={buyTransaction?.monetaryValue}
                     />
                  );
               })} */}
         </ul>
         <div className={styles.navigationPagesContainer}>
            <Button type="button">Página Anterior</Button>
            <Button type="button">Próxima Página</Button>
         </div>
      </div>
   );
}

export default WalletCardList;
